
-- Create the tech_companies database
CREATE DATABASE IF NOT EXISTS tech_companies_db;

-- Use the tech_companies database
USE tech_companies_db;

-- Create the tech_companies data table
CREATE TABLE IF NOT EXISTS tech_companies_data (
    Ranking INT PRIMARY KEY,
    Company VARCHAR(255),
    Market_Cap FLOAT,
    Stock VARCHAR(10),
    Country VARCHAR(100),
    Sector VARCHAR(100),
    Industry VARCHAR(255)
);

